"use strict";

// estructura if, else if, else
var num = 25;

if (num == -7) {
  console.log("Viva la pepa");
} else if (num < 0) {
  console.log("Es negativo");
} else if (num == 0) {
  console.log("Es cero");
} else if (num == 25) {
  console.log("Te la hinco");
} else {
  console.log("Es positivo");
}